mapper.select:
  		select u.id, u.username, u.email, u.mobile, u.is_active, u.create_time,
  		u.gender, d.dept_name from sys_user u 
  		left join sys_dept d on u.dept_id = d.id
  		where 1=1

        	and u.username = #{username}


	        and d.id = #{deptId}


	       and u.gender = #{gender}


	        and u.is_active = #{is_active}

  		order by u.id
